package com.hcemanager.models.entities;import com.hcemanager.models.codes.Code;/** * A subtype of LivingSubjectDAO representing a human being. * * Constraints: This class can be used to represent either a single individual * or a group of individuals based on the valueObservation * of Entity.determinerCode and Entity.quantity. * * @author Daniel Bellon & Juan Diaz */public class Person extends LivingSubject {    protected Code maritalStatusCode;    protected Code educationLevelCode;    protected Code raceCode;    protected Code disabilityCode;    protected Code livingArrangementCode;    protected Code religiousAffiliationCode;    protected Code ethnicGroupCode;    protected String addr;    protected String idPerson;    public Code getMaritalStatusCode() {        return maritalStatusCode;    }    public void setMaritalStatusCode(Code maritalStatusCode) {        this.maritalStatusCode = maritalStatusCode;    }    public Code getEducationLevelCode() {        return educationLevelCode;    }    public void setEducationLevelCode(Code educationLevelCode) {        this.educationLevelCode = educationLevelCode;    }    public Code getRaceCode() {        return raceCode;    }    public void setRaceCode(Code raceCode) {        this.raceCode = raceCode;    }    public Code getDisabilityCode() {        return disabilityCode;    }    public void setDisabilityCode(Code disabilityCode) {        this.disabilityCode = disabilityCode;    }    public Code getLivingArrangementCode() {        return livingArrangementCode;    }    public void setLivingArrangementCode(Code livingArrangementCode) {        this.livingArrangementCode = livingArrangementCode;    }    public Code getReligiousAffiliationCode() {        return religiousAffiliationCode;    }    public void setReligiousAffiliationCode(Code religiousAffiliationCode) {        this.religiousAffiliationCode = religiousAffiliationCode;    }    public Code getEthnicGroupCode() {        return ethnicGroupCode;    }    public void setEthnicGroupCode(Code ethnicGroupCode) {        this.ethnicGroupCode = ethnicGroupCode;    }    public String getAddr() {        return addr;    }    public void setAddr(String addr) {        this.addr = addr;    }    public String getIdPerson() {        return idPerson;    }    public void setIdPerson(String id) {        this.idPerson = id;    }}